'use strict';

const Command = require('lub-command');

class StartCommand  extends Command{
    constructor(rawArgv,config) {
        super(rawArgv,config)
        this.usage = "Usage: start command usage";
        this.options = {
            depth: {
                description: 'level to start',
                type: 'number',
                default: 1,
            },
        };
    }

    get description() {
        return "start command"
    }
    
    run(_,config){
        console.log('start config[env] is',config.env)
    }
}

module.exports = StartCommand